-- Getting the mapID
SELECT 
	id,
    JSON_EXTRACT(jsondata, '$.info.mapId') AS mapId
FROM matches;

-- Getting the gameMode
SELECT 
	id,
    JSON_EXTRACT(jsondata, '$.info.gameMode') AS gameMode
FROM matches;

-- Getting the Kills of the second Player
SELECT 
	id, 
	JSON_EXTRACT(jsondata, '$.info.participants[1].riotIdGameName') AS name,
	JSON_EXTRACT(jsondata, '$.info.participants[1].kills') AS kills 
FROM matches;

-- Getting the KDA of a player named 'Kialos'
SELECT
	id,
    players.riotIdGameName,
	players.win,
    players.kills,
    players.assists,
    players.deaths,
    (players.kills + players.assists) / NULLIF(players.deaths, 0) AS kda
FROM matches,
JSON_TABLE(
	jsondata,
    '$.info.participants[*]'
    COLUMNS (
		riotIdGameName VARCHAR(50) PATH '$.riotIdGameName',
        kills INT PATH '$.kills',
        assists INT PATH '$.assists',
        deaths INT PATH '$.deaths',
        win INT PATH '$.win'
    )
) AS players
WHERE players.riotIdGameName = 'Kialos';